     const producto = productos.filter(producto => producto.id === id)
    agregarAlCarrito([
        ...carrito, producto
    ]);
    }
    return ( 
        <div>
        <h1>Desde producto {nombre}</h1>
        <p>precio: ${precio}</p>
        <button
        type="button" onClick={() => agregarProducto(id)}>
            agregar al carrito
